def decipher(string: str) -> int:
    level = 0
    for letter in string:
        if letter == "(":
            level += 1
        
        if letter == ")":
            level -= 1
    return level

def findLevel(string: str, levelToFind) -> str:
    level = 0
    position = 0
    for letter in string:
        position += 1
        if letter == "(":
            level += 1
        
        if letter == ")":
            level -= 1

        if level == levelToFind:
            return position

